mapper.update:
UPDATE
    board
SET
    board_state = #{state}
WHERE
    board_id = #{boardId}

mapper.select:
SELECT COUNT(*) FROM board b LEFT JOIN report r ON b.board_id = r.reported_content_id AND r.report_category = "board" LEFT JOIN board_large_category lc ON b.large_category_id = lc.large_category_id LEFT JOIN board_small_category sc ON b.small_category_index = sc.small_category_index WHERE b.large_category_id != 3
AND b.board_id = r.reported_content_id
AND b.large_category_id = #{totalSelectValue}
AND b.large_category_id = #{totalSelectValue} AND b.small_category_index = #{smallSelectValue}
AND b.subject LIKE CONCAT('%', #{searchValue}, '%')
